// Parse CrewAI response and execute actions
const crewAIResponse = $input.all()[0].json;
const actions = crewAIResponse.actions || [];
const results = [];

// Store actions for execution
return [{
  json: {
    crewAIResponse: crewAIResponse,
    actions: actions,
    executionStartTime: new Date().toISOString()
  }
}];

// Execute actions from CrewAI
const data = $input.all()[0].json;
const actions = data.actions || [];
const executionResults = [];

// Log actions to be executed
for (const action of actions) {
  executionResults.push({
    action: action.type,
    status: 'pending',
    details: action.details
  });
}

// Note: Actual action execution would be handled by subsequent nodes
// based on action types (e.g., process_invoice, generate_report, update_payroll, etc.)

return [{
  json: {
    ...data,
    actionsPending: executionResults,
    readyForExecution: true
  }
}];

// Format final response
const data = $input.all()[0].json;

return [{
  json: {
    success: true,
    department: 'backoffice',
    timestamp: new Date().toISOString(),
    crewAIResponse: data.crewAIResponse.response || data.crewAIResponse,
    actionsRequested: data.actions.length,
    message: 'Back Office department workflow completed. CrewAI instructions processed.'
  }
}];
