// Define workflows to monitor
const workflows = [
  { name: 'Main API Gateway', webhook: 'chat', type: 'critical' },
  { name: 'Chief AI Agent', webhook: 'chief-ai-agent', type: 'critical' },
  { name: 'API Bridge', webhook: 'api-bridge', type: 'critical' },
  { name: 'Knowledge Base', webhook: 'knowledge-base', type: 'normal' },
  { name: 'MCP Tools', webhook: 'mcp-tools', type: 'normal' }
];

return workflows.map(wf => ({json: wf}));

// Process health check results
const input = $json;
const workflow = input.name ? input : {name: 'Unknown', webhook: 'Unknown', type: 'Unknown'};

// Get data from previous HTTP request node
const httpNode = $input.context['Health Check Webhook'];
const statusCode = httpNode?.statusCode || (httpNode?.error ? 0 : 200);
const responseTime = httpNode?.responseTime || 0;
const error = httpNode?.error || null;

// Determine health status
let status = 'healthy';
let healthScore = 100;
let issues = [];

if (error || statusCode === 0) {
  status = 'unhealthy';
  healthScore = 0;
  issues.push(error ? error.message : 'Webhook unreachable');
} else if (statusCode !== 200) {
  status = 'degraded';
  healthScore = 50;
  issues.push(`HTTP ${statusCode}`);
} else if (responseTime > 3000) {
  status = 'degraded';
  healthScore = 70;
  issues.push(`Slow response: ${responseTime}ms`);
}

return {
  json: {
    timestamp: new Date().toISOString(),
    workflow: workflow.name,
    webhook: workflow.webhook,
    type: workflow.type,
    status: status,
    healthScore: healthScore,
    statusCode: statusCode,
    responseTime: responseTime,
    issues: issues
  }
};

// Aggregate all health checks
const healthRecords = $input.all().map(item => item.json);

// Calculate overall system health
const totalScore = healthRecords.reduce((sum, record) => sum + record.healthScore, 0);
const avgScore = totalScore / healthRecords.length;

// Count statuses
const statusCounts = healthRecords.reduce((counts, record) => {
  counts[record.status] = (counts[record.status] || 0) + 1;
  return counts;
}, {});

// Identify critical issues
const criticalIssues = healthRecords
  .filter(r => r.type === 'critical' && r.status !== 'healthy')
  .map(r => ({ workflow: r.workflow, status: r.status, issues: r.issues }));

// Create monitoring report
const report = {
  timestamp: new Date().toISOString(),
  overall: {
    healthScore: Math.round(avgScore),
    status: avgScore >= 90 ? 'healthy' : avgScore >= 70 ? 'degraded' : 'unhealthy',
    totalWorkflows: healthRecords.length,
    healthy: statusCounts.healthy || 0,
    degraded: statusCounts.degraded || 0,
    unhealthy: statusCounts.unhealthy || 0
  },
  criticalIssues: criticalIssues,
  allWorkflows: healthRecords
};

console.log('Monitoring Report:', JSON.stringify(report, null, 2));

return [{ json: report }];

// Log alert (in production, this would send notifications)
const report = $input.all()[0].json;

console.log('⚠️ SYSTEM ALERT ⚠️');
console.log(`Overall Health: ${report.overall.status} (${report.overall.healthScore}%)`);
console.log(`Critical Issues: ${report.criticalIssues.length}`);

if (report.criticalIssues.length > 0) {
  console.log('\nCritical Workflows Down:');
  report.criticalIssues.forEach(issue => {
    console.log(`- ${issue.workflow}: ${issue.status}`);
    issue.issues.forEach(i => console.log(`  → ${i}`));
  });
}

// In production, you would:
// 1. Send email alerts
// 2. Post to Slack
// 3. Create incident tickets
// 4. Store in monitoring database

return [{
  json: {
    alertSent: true,
    severity: report.overall.healthScore < 50 ? 'critical' : 'warning',
    message: `System health degraded to ${report.overall.status}`,
    report: report
  }
}];

// Store monitoring data (placeholder for database storage)
const report = $input.all()[0].json;

console.log(`✅ Monitoring cycle completed at ${report.timestamp}`);
console.log(`System Status: ${report.overall.status}`);
console.log(`Next check in 15 minutes...`);

// In production, store this in a time-series database
// for historical tracking and dashboards

return [{ json: { stored: true, report: report } }];
