const apiResponse = $input.all()[0].json;
const timestamp = new Date().toISOString();

// Build professional health check response
return [{
  json: {
    status: "healthy",
    service: "agentic-system-main-api",
    timestamp: timestamp,
    checks: {
      api_gateway: {
        status: apiResponse.system_status === "operational" ? "up" : "down",
        system_status: apiResponse.system_status
      },
      ai_agents: {
        status: "up",
        available_departments: apiResponse.agents?.available_departments || [],
        architecture: apiResponse.architecture
      },
      knowledge_base: {
        status: apiResponse.privategpt?.status === "connected" ? "up" : "down",
        documents: apiResponse.privategpt?.documents || 0
      }
    },
    version: "1.0.0",
    environment: "production"
  }
}];
